export default function App() {
  const [currentPage, setCurrentPage] = useState('login');
  const [user, setUser] = useState(null);
  const [selectedQuizId, setSelectedQuizId] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    const role = localStorage.getItem('role');
    if (token && role) {
      setUser({ username: 'user', role });
      setCurrentPage('dashboard');
    }
  }, []);

  const handleLogin = (userData) => {
    setUser(userData);
    setCurrentPage('dashboard');
  };

  const handleRegister = (userData) => {
    setUser(userData);
    setCurrentPage('dashboard');
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('role');
    setUser(null);
    setCurrentPage('login');
  };

  const handleTakeQuiz = (quizId) => {
    setSelectedQuizId(quizId);
    setCurrentPage('quiz');
  };

  const handleViewQuestions = () => {
    setCurrentPage('questions');
  };

  // Render current page
  switch (currentPage) {
    case 'login':
      return <Login onLogin={handleLogin} onSwitchToRegister={() => setCurrentPage('register')} />;
    
    case 'register':
      return <Register onRegister={handleRegister} onSwitchToLogin={() => setCurrentPage('login')} />;
    
    case 'dashboard':
      return (
        <UserDashboard
          user={user}
          onLogout={handleLogout}
          onTakeQuiz={handleTakeQuiz}
          onViewQuestions={handleViewQuestions}
        />
      );
    
    case 'quiz':
      return (
        <div className="min-h-screen flex items-center justify-center">
          <Card variant="neon" className="p-8 text-center">
            <h2 className="text-2xl font-bold text-gradient-electric mb-4">Quiz Feature Coming Soon! ðŸš€</h2>
