          </Card>
        </div>
      );
    
    case 'questions':
      return (
        <div className="min-h-screen flex items-center justify-center">
          <Card variant="neon" className="p-8 text-center">
            <h2 className="text-2xl font-bold text-gradient-electric mb-4">Question Management Coming Soon! âš™ï¸</h2>
